    @Factory
    public static Object[] createInstances() {
        Object[] result = new Object[]{
                new RegularUser(createUserFactoryConfig(USERNAME, Constants.Permissions.EVERYTHING, false, userExpectedEverything)),
                new RegularUser(createUserFactoryConfig(USERNAME, Constants.Permissions.READ, false, userExpectedRead)),
                new RegularUser(createUserFactoryConfig(CUSTOM_GROUP, Constants.Permissions.EVERYTHING, true, groupExpectedEverything)),
                new RegularUser(createUserFactoryConfig(CUSTOM_GROUP, Constants.Permissions.READ, true, groupExpectedRead)),
        };
        return result;
    }

