    @Authentication(username = USERNAME, password = PASSWORD)
    @Test(description = "Create group with name: " + CUSTOM_GROUP, priority = 1)
    public void createCustomGroup() throws JSONException {
        log.info("Create new group with name: {}", CUSTOM_GROUP);
        super.createOrReuseGroup(CUSTOM_GROUP, USERNAME);
    }

    @Authentication(username = USERNAME, password = PASSWORD)
    @Test(description = "Create or reuse automationuseranalytic with analytics role and group: " + CUSTOM_GROUP,
            dependsOnMethods = "createCustomGroup",
            priority = 2)
    public void createUser() {
        log.info("Create user with name: {}, if not exist. Assign him to group: {}", USERNAME, CUSTOM_GROUP);
        super.createOrReuseUser(USERNAME, PASSWORD, List.of(ANALYTICS), List.of(CUSTOM_GROUP), true);
        await().pollInterval(POOL_INTERVAL, TimeUnit.SECONDS).atMost(60, TimeUnit.SECONDS);
        List<UserDataDTO> users = userManagementClient.getAllUsers().as(new TypeRef<>() {
        });

        UserDataDTO foundUser = users
                .stream()
                .filter(user -> user.getUsername().equals(USERNAME))
                .findFirst()
                .orElse(null);

        if (foundUser != null) {
            List<String> groups = foundUser.getGroups();
            Assert.assertTrue(groups.contains(CUSTOM_GROUP),
                    "User " + USERNAME + " is NOT assigned to group " + CUSTOM_GROUP);
        }
    }

    @Authentication(username = USERNAME, password = PASSWORD)
    @Test(description = "Create case with automationuser and prepare for tests",
            priority = 3)
    public void createCaseAndAssignRights(ITestContext context) throws JsonProcessingException {
        Response caseResponse = layoutServiceClient.getAllProjects(ADMIN_PROJECT_NAME);
        LayoutDTO[] cases = caseResponse.getBody().as(LayoutDTO[].class);
        if (cases.length > 0) {
            log.info("Case with name {} already exists. Deleting it to create a new one for tests.", ADMIN_PROJECT_NAME);
            String caseId = cases[0].getId();
            super.deleteProjectById(caseId, USERNAME, PASSWORD, true);
        }
        log.info("login with {}, create case with name {} and create artefacts and CR in it to be used in the tests",
                USERNAME, ADMIN_PROJECT_NAME);
        String caseId = super.createOrReuseCase(ADMIN_PROJECT_NAME, context);

        log.info("Check the name of the case");
        LayoutDTO adminCase = super.getProjectById(caseId, true);
        Assert.assertEquals(adminCase.getName(), ADMIN_PROJECT_NAME);



        log.info("Set caseId {} to context", caseId);
        context.setAttribute(ADMIN_PROJECT_ID, caseId);
    }

